import JsonPopulator from '../../../utils/JsonPopulator'

function ComponentLoader(props) {
  for (const compName in props.components) {
    const Component = props.components[compName];
    props.components[compName] = (comp,id) => {
      id = id?id:"";
      return <Component id={comp.id+id} {...comp.props}/>;
    }
  }
  new JsonPopulator(props.components).populatePageStructure(props.pageStructure);
  return props.pageStructure.render(props.pageStructure);
}

export default ComponentLoader;
