export default class JsonPopulator {

  constructor(properties){
    this.properties = properties;
  }

  populatePageStructure(json){
    this.populateComponent(json);
  }

  populateComponent(json){
    if(!json.includes("render")) throw new ReactIOCException("Missing 'render' attribute!", "'render' attribute is missing in json.");
    this.populateProp(json,"render");
    const propsToPopulate = this.getPropsToPopulate(json);
    if(json.includes("props")) this.populateProps(json["props"],propsToPopulate)
  }

  populateProps(props,propsToPopulate){
    for (const prop in props) {
      if (propsToPopulate.includes(prop)) this.populateProp(props, prop);
    }
  }

  populateProp(json, prop) {
    const propValue = json[prop];
    if (typeof propValue === "object") this.populatePropObject(propValue);
    else json[prop] = this.properties[json[prop]];
  }

  populatePropObject(propValue){
    if (Array.isArray(propValue)) this.loopJsonArray(propValue);
    else if (this.isComponent(propValue)) this.populateComponent(propValue)
    else this.loopJson(propValue);
  }

  loopJsonArray(jsonArray) {
    jsonArray.forEach((prop) => {
      if (typeof prop === "object")this.populatePropObject(prop);
    });
  }

  loopJson(json) {
    for (const prop in json) {
      if (typeof prop === "object")this.populatePropObject(prop);
    }
  }

  static getPropsToPopulate(json){
    let propsToPopulate = json["propsToPopulate"];
    json["propsToPopulate"] = [];
    if(!propsToPopulate) propsToPopulate = [];
    return propsToPopulate;
  }

  static isComponent(json){
    return json.includes("render");
  }
}
