import JsonPopulator from '../../../utils/JsonPopulator';
import { useDispatch, useSelector } from "react-redux";
import { reactIocActionNames } from '../../../redux/confiReducer/reactIocActions';
import { useEffect, useMemo } from 'react';

function prepareComponents(components) {
  const populatedComponents = { ...components }
  for (const compName in populatedComponents) {
    const Component = populatedComponents[compName];
    populatedComponents[compName] = (comp, parentId, suffixId) => {
      parentId = parentId ? parentId + "/" : "";
      suffixId = suffixId ? suffixId : "";
      const key = parentId + comp.id + suffixId;
      return <Component key={key} id={key} {...comp.props} />;
    }
  }
  return populatedComponents;
}

function populatePageStructure(pageStructure, components, functions, resources) {
  const populatedPageStructure = { ...pageStructure }
  const data = { ...components, ...functions, ...resources }
  const newState = new JsonPopulator(data).populatePageStructure(populatedPageStructure);
  return [newState, populatedPageStructure];
}


function ComponentLoader(props) {
  const state = useSelector((state) => state);
  const dispatch = useDispatch();
  const components = useMemo(() => prepareComponents(props.components), [props.components]);
  const [newState, pageStructure] = useMemo(() => populatePageStructure(props.pageStructure, components, props.functions, props.resources), [props.pageStructure]);
  useEffect(
    () => {
      dispatch({
        type: reactIocActionNames.populateState,
        newState: newState
      });
    }, [dispatch, newState]
  );
  if (state === null) return <></>
  return pageStructure.render(pageStructure);
}

export default ComponentLoader;
