import JsonPopulator from '../../../utils/JsonPopulator';
import { useDispatch, useSelector } from "react-redux";
import { reactIocActionNames } from '../../../redux/confiReducer/reactIocActions';
import { useEffect, useRef } from 'react';

function prepareComponents(components){
  for (const compName in components) {
    const Component = components[compName];
    components[compName] = (comp,parentId,suffixId) => {
      parentId = parentId?parentId+"/":"";
      suffixId = suffixId?suffixId:"";
      const key = parentId + comp.id + suffixId;
      return <Component key={key} id={key} {...comp.props}/>;
    }
  }
}


function ComponentLoader(props) {
  const state = useSelector((state) => state);
  const dispatch = useDispatch();
  prepareComponents(props.components);
  const data = {...props.components,...props.functions,...props.resources};
  const newState = new JsonPopulator(data).populatePageStructure(props.pageStructure);
  const pageStructure = useRef(props.pageStructure);
  useEffect(
    () => {
      dispatch({
        type: reactIocActionNames.populateState,
        newState:newState
      });
    },[dispatch,newState]
  );
  if(state === null) return <></>
  return pageStructure.render(pageStructure);
}

export default ComponentLoader;
