import JsonPopulator from '../../../utils/JsonPopulator';
import { useDispatch } from "react-redux";

function ComponentLoader(props) {
  const dispatch = useDispatch();
  for (const compName in props.components) {
    const Component = props.components[compName];
    props.components[compName] = (comp,parentId,suffixId) => {
      parentId = parentId?parentId+"/":"";
      suffixId = suffixId?suffixId:"";
      const key = parentId + comp.id + suffixId;
      return <Component key={key} id={key} {...comp.props}/>;
    }
  }
  const data = {...props.components,...props.functions,...props.resources};
  const state = new JsonPopulator(data).populatePageStructure(props.pageStructure);

  return props.pageStructure.render(props.pageStructure);
}

export default ComponentLoader;
